    <node TEXT="分类" ID="IMjZekQi8C" _mubu_text="%3Cspan%3E%E5%88%86%E7%B1%BB%3C/span%3E" STYLE="bubble" POSITION="right">
      <node TEXT="反射型" ID="FHxlsbrEz6" _mubu_text="%3Cspan%20class=%22%20text-color-yellow%22%3E%E5%8F%8D%E5%B0%84%E5%9E%8B%3C/span%3E" STYLE="fork">
        <node TEXT="又称非持久型XSS，这种攻击方式往往具有一次性，只在用户单击时触发。常见注入点网站的搜索栏、用户登录入口、输入表单等地方，常用来窃取客户端cookies或钓鱼欺骗。邮件等形式包含xss代码，用户点击时，服务器处理，触发XSS攻击方式攻击者通过电子邮件等方式将包含XSS代码的恶意链接发送给目标用户。当目标用户访问该链接时，服务器接受该目标用户的请求并进行处理，然后服务器把带有XSS的代码发送给目标用户的浏览器，          浏览器解析这段带有XSS代码的恶意脚本后，就会触发XSS漏洞。" ID="vPWGjCXX24" _mubu_text="%3Cspan%3E%E5%8F%88%E7%A7%B0%E9%9D%9E%E6%8C%81%E4%B9%85%E5%9E%8BXSS%EF%BC%8C%E8%BF%99%E7%A7%8D%E6%94%BB%E5%87%BB%E6%96%B9%E5%BC%8F%E5%BE%80%E5%BE%80%E5%85%B7%E6%9C%89%E4%B8%80%E6%AC%A1%E6%80%A7%EF%BC%8C%E5%8F%AA%E5%9C%A8%E7%94%A8%E6%88%B7%E5%8D%95%E5%87%BB%E6%97%B6%E8%A7%A6%E5%8F%91%E3%80%82%E5%B8%B8%E8%A7%81%E6%B3%A8%E5%85%A5%E7%82%B9%E7%BD%91%E7%AB%99%E7%9A%84%E6%90%9C%E7%B4%A2%E6%A0%8F%E3%80%81%E7%94%A8%E6%88%B7%E7%99%BB%E5%BD%95%E5%85%A5%E5%8F%A3%E3%80%81%E8%BE%93%E5%85%A5%E8%A1%A8%E5%8D%95%E7%AD%89%E5%9C%B0%E6%96%B9%EF%BC%8C%E5%B8%B8%E7%94%A8%E6%9D%A5%E7%AA%83%E5%8F%96%E5%AE%A2%E6%88%B7%E7%AB%AFcookies%E6%88%96%E9%92%93%E9%B1%BC%E6%AC%BA%E9%AA%97%E3%80%82%E9%82%AE%E4%BB%B6%E7%AD%89%E5%BD%A2%E5%BC%8F%E5%8C%85%E5%90%ABxss%E4%BB%A3%E7%A0%81%EF%BC%8C%E7%94%A8%E6%88%B7%E7%82%B9%E5%87%BB%E6%97%B6%EF%BC%8C%E6%9C%8D%E5%8A%A1%E5%99%A8%E5%A4%84%E7%90%86%EF%BC%8C%E8%A7%A6%E5%8F%91XSS%E6%94%BB%E5%87%BB%E6%96%B9%E5%BC%8F%E6%94%BB%E5%87%BB%E8%80%85%E9%80%9A%E8%BF%87%E7%94%B5%E5%AD%90%E9%82%AE%E4%BB%B6%E7%AD%89%E6%96%B9%E5%BC%8F%E5%B0%86%E5%8C%85%E5%90%ABXSS%E4%BB%A3%E7%A0%81%E7%9A%84%E6%81%B6%E6%84%8F%E9%93%BE%E6%8E%A5%E5%8F%91%E9%80%81%E7%BB%99%E7%9B%AE%E6%A0%87%E7%94%A8%E6%88%B7%E3%80%82%E5%BD%93%E7%9B%AE%E6%A0%87%E7%94%A8%E6%88%B7%E8%AE%BF%E9%97%AE%E8%AF%A5%E9%93%BE%E6%8E%A5%E6%97%B6%EF%BC%8C%E6%9C%8D%E5%8A%A1%E5%99%A8%E6%8E%A5%E5%8F%97%E8%AF%A5%E7%9B%AE%E6%A0%87%E7%94%A8%E6%88%B7%E7%9A%84%E8%AF%B7%E6%B1%82%E5%B9%B6%E8%BF%9B%E8%A1%8C%E5%A4%84%E7%90%86%EF%BC%8C%E7%84%B6%E5%90%8E%E6%9C%8D%E5%8A%A1%E5%99%A8%E6%8A%8A%E5%B8%A6%E6%9C%89XSS%E7%9A%84%E4%BB%A3%E7%A0%81%E5%8F%91%E9%80%81%E7%BB%99%E7%9B%AE%E6%A0%87%E7%94%A8%E6%88%B7%E7%9A%84%E6%B5%8F%E8%A7%88%E5%99%A8%EF%BC%8C%20%20%20%20%20